import React,{useState,useEffect,useRef} from 'react'
import axios from 'axios';
import {Link} from 'react-router-dom';
import {useHistory} from 'react-router-dom';
import classes from './Login.module.css';

const Register=()=> {

    const [user, setUser]=useState({email:"", password:"",address:""})
    const [msg,setMsg] = useState('');
    const mailInputRef=useRef();
    const addressInputRef= useRef();
    const passInputRef= use
    const history =useHistory();

    useEffect(()=>{
        //console.log("register rendered");
         if(msg.includes("Success")){
            setTimeout(()=>{ history.push('/login');setMsg(''); },1000);            
         }
},[msg,history])

    const changeMe=(event)=>{
        const {name , value} = event.target ;
         setUser({
             ...user, [name]:value
         });
         console.log("user",user);
    }

    const success ={padding:"10px 15px", border:"1px solid green", color:"green"}
    const fail ={padding:"10px 15px", border:"1px solid red", color:"red"}

    // const registerMe=(e)=>{

    //     e.preventDefault();
    //     //api post to server
    //     axios.post('https://my-json-yumito-server.herokuapp.com/users',user)
    //     .then(res =>{
    //         setMsg("Registered Successfully");
    //        //redirect to login page
    //     })
    //     .catch((e)=>{
    //         setMsg("Something went wrong. Please try again later");
    //     })
    // }

  const onRegisteredHandler=(event)=>{
   event.preventDefault();
   console.log("user",user)
  }
    return (
        <form className={`${classes.auth} form1`} onSubmit={onRegisteredHandler}>
                    <h2>Create Account</h2><br/>
                    <div className="d-flex flex-column">
                    <div className="form-group">
             <div className={`${classes.control} form-group`}>
             <label htmlFor="email" className="form-label m-2 h5">Email</label>
            <input type="email" name="mail" placeholder="Email" className="form-control"  onChange={changeMe} required/>
            </div>
             <div className={`${classes.control} form-group`}>
             <label htmlFor="password" className="form-label m-2 h5">Password</label>
            <input type="password" name="psd" placeholder="Password" className="form-control"  onChange={changeMe} required/>
            </div>
            <div className={`${classes.control} form-group`}>
            <label htmlFor="address" className="form-label m-2 h5">Address</label>
             <textarea type="text" name="addr" placeholder="Address" className={`${classes.control} form-group`} onChange={changeMe} required/>
            </div>         
             </div>
                <button className="btn btn-primary my-3 h4">Sign Up</button>
                <div className="h5 text-center" style={msg==='' ? {} : (msg.includes("Success") ? success :fail)}>{msg}</div>
                <hr/>
                 <span>Already have an account?  |  <Link to="/login">Sign in</Link></span>              
                 </div>

           </form>
    )
}

export default React.memo(Register)
