import React,{useEffect,useContext,useState} from 'react';
import {itemContext} from '../../store/context';
import axios from 'axios';
import {Link,useHistory} from 'react-router-dom';
import classes from './Login.module.css';

var store = require('store');

const Login = ()=>{
    const iL = useContext(itemContext);
    const history=useHistory();
    const loggedin = store.get('loggedIn');
    const {loggedIn}=iL.state ?? (loggedin ?? false);
    const [userLogin, setUserLogin]=useState({mail:"", psd:""}) 
    const orderedItems = store.get('orderedItems');
    let flag=false;


    useEffect(()=>{
        //navigate to specific page after login
   if(loggedIn===true){
       if(orderedItems && orderedItems.length){
       //got to checkout page
       //flag=true;
       setTimeout(()=>{ history.replace('/checkout')},1000);
   }
   else{
       //go to menu page
       history.replace('/');
   }
 }       
   },[loggedIn,history,orderedItems]);


   const onLoginRequest=async()=>{
    const response = await fetch('https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyBFDRS3qMecD0q0D-7hohPURiooqwJOifU',{
        method: 'POST',
        headers: {
            'Content-type': '/application.json',
        },
        body: JSON.stringify({
              email:user.email,
              password: user.password,
              returnSecureToken:true,
          })
    })
   }

   const onLoginHandler=(event)=>{
       event.preventDefault();
       onLoginRequest();
   }


//     const checkCred=(e)=>{
//         flag=true;
//      e.preventDefault();
//      //api call to fetch users
//      axios.get("https://my-json-yumito-server.herokuapp.com/users")
//       .then(response =>{
//           flag=false;
//      iL.method( {type:'checkUser', payload: response.data ,user:userLogin});
//    })
//  .catch((e)=>{
//      flag=false;
//   alert("Please enter valid credentials")
//  })

//  }

    const loginMe =(e)=>{
        const {name , value} = e.target ;
        setUserLogin({
            ...userLogin, [name]:value
    })
}

const onRegisterHandler=()=>{
    console.log("registered!");
    window.location.replace("/register");
}

    return(
       <>
          {flag===true && <h4 className="text-success text-center p-1">Redirecting to...</h4>}
        <form className={`${classes.auth} form1`} onSubmit={onLoginHandler}>
       <h2>Login</h2><br/>
       <div className={`${classes.control} form-group d-flex flex-column g-2`}>
       <label htmlFor="email" className="form-label m-2 h5">Email</label>
       <input type="email" name="mail" className="form-control-lg" value={userLogin.mail} onChange={loginMe} placeholder="Email" required/>
       </div>
       <div className={`${classes.control} form-group d-flex flex-column g-2`}>
        <label htmlFor="password" className="form-label m-2 h5">Password</label>
        <input type="password" name="psd" className="form-control-lg" value={userLogin.psd} onChange={loginMe} placeholder="Password" required/>
        </div>
        <button className="btn btn-primary btn-lg m-3 ms-0" >Sign in</button>
        <hr/>
        <span>Don't have an account?  |   <Link to="/register"><span onClick={onRegisterHandler}>Register Here</span></Link></span> 
       </form>
       </>
    )
}

export default Login;
